import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomeList from './components/HomeList';
import HomeDetails from './components/HomeDetails';
import homesData from './data/homes.json'; // Import the homes data

function App() {
  const [homes, setHomes] = useState([]);

  useEffect(() => {
    // Fetch or set your data here
    setHomes(homesData); // Set the data to the state
  }, []);

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<HomeList homes={homesData} />} /> {/* Pass the 'homes' data as a prop */}
          <Route path="/homedetails/:id" element={<HomeDetails />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;




