// HomeList.js
import React, { useState } from 'react';
import SortHomes from './SortHomes';
import HomeDetails from './HomeDetails'; // Import the HomeDetails component

function HomeList({ homes, onHomeClick, selectedHome, onCloseDetails }) {
    const [sortedHomes, setSortedHomes] = useState([...homes]);

    const handleSort = ({ criteria, order }) => {
        const sorted = [...sortedHomes].sort((a, b) => {
            if (criteria === 'price') {
                const priceA = parseFloat(a.price.replace('$', '').replace(',', ''));
                const priceB = parseFloat(b.price.replace('$', '').replace(',', ''));
                return order === 'asc' ? priceA - priceB : priceB - priceA;
            } else if (criteria === 'registered') {
                const dateA = new Date(a.registered).getTime();
                const dateB = new Date(b.registered).getTime();
                return order === 'asc' ? dateA - dateB : dateB - dateA;
            }
            return 0; // Default, no sorting
        });

        setSortedHomes(sorted);
    };

    return (
        <div>
            <SortHomes onSort={handleSort} />
            {sortedHomes.map((home) => (
                <div key={home.id} data-testid="home">
                    <img
                        src={home.image_url}
                        alt={`Home ${home.id}`}
                        data-testid="home-image"
                        style={{ maxWidth: '200px', maxHeight: '150px' }}
                    />
                    <p data-testid="home-price">Price: {home.price}</p>
                    <p data-testid="home-address">Address: {home.address}</p>
                    <Link to={`/homedetails/${home.id}`}>View Details</Link> {/* Use Link */}
                </div>
            ))}
        </div>
    );
}

export default HomeList;


