import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import homesData from '../data/homes.json'; // Import the homes data

function HomeDetails() {
    const { id } = useParams();
    const [homeDetails, setHomeDetails] = useState(null);

    // Use an effect to fetch home details based on the ID when the component mounts
    useEffect(() => {
        // Replace this with your actual API call or data retrieval logic
        // You can use the `id` parameter from the URL to fetch the specific home details
        const fetchHomeDetails = async () => {
            try {
                // In this example, we filter the data from the imported JSON file
                const homeData = homesData.find(home => home.id === id);
                if (!homeData) {
                    throw new Error('Home not found');
                }
                setHomeDetails(homeData); // Set the fetched home details in state
            } catch (error) {
                console.error('Error fetching home details:', error);
            }
        };

        fetchHomeDetails();
    }, [id]);

    // Render loading state while fetching data
    if (!homeDetails) {
        return <div>Loading...</div>;
    }

    // Render home details when data is available
    return (
        <div>
            <h2>Home Details</h2>
            <img src={homeDetails.image_url} alt={homeDetails.address} />
            <p>Price: {homeDetails.price}</p>
            <p>Address: {homeDetails.address}</p>
            <p>Registered: {homeDetails.registered}</p>
            <p>About: {homeDetails.about}</p>
            <p>Realtor: {homeDetails.realtor.first} {homeDetails.realtor.last}</p>
            <p>Email: {homeDetails.realtor.email}</p>
            <p>Phone: {homeDetails.realtor.phone}</p>
        </div>
    );
}

export default HomeDetails;
